import axios from 'axios';

const BASE_API_URL = 'http://localhost:5000';

/* 
  json-server will give you CRUD endpoints on snacks and drinks.
  Here we've provided you with a single action to get all drinks.

  You'll need to add to this class as you build features for the app.
*/

class SnackOrBoozeApi {
   static async getSnacks() {
      const result = await axios.get(`${BASE_API_URL}/snacks`);
      return result.data;
   }

   static async getDrinks() {
      const result = await axios.get(`${BASE_API_URL}/drinks`);
      return result.data;
   }

   static async makeDrink() {
      const result = await axios.post(`${BASE_API_URL}/drinks`);
      return result.data;
   }

   static async addDrink(drink) {
      const newDrink = {
         id: 'mojito',
         name: 'Mojito',
         description: 'A refreshing Cuban classic with a minty twist.',
         recipe:
            'Muddle mint leaves with sugar and lime juice. Add rum and soda water.',
         serve: 'Serve over ice, garnished with mint leaves.',
      };

      try {
         const result = await request
            .post('http://localhost:3000/drinks')
            .send(newDrink);
         console.log('Drink added:', result.body);
      } catch (error) {
         console.error('Error adding drink:', error);
      }
   }
}

export default SnackOrBoozeApi;
