import { useState } from 'react';
import { useHistory } from 'react-router-dom';
import SnackOrBoozeApi from './Api';
const NewItemForm = ({ addColor }) => {
   // NOTE: have a toggle for if it is a drink or snack?
   const INITIAL_FORM_STATE = {
      id: '',
      name: '',
      description: '',
      recipe: '',
      serve: '',
   };
   const [formData, setFormData] = useState(INITIAL_FORM_STATE);
   const [items, setItems] = useState([]);

   const handleChange = (event) => {
      const { name, value } = event.target; //grabbing both the value and the input 'name' property
      //the [name] as the key is used to set name as an inital key | used when creating an object so we don't have to initialize it before
      setFormData((formData) => ({
         // the formData state is being updated with existing formData (...spread) and then the new name (associated with each input) and value from that input
         ...formData,
         [name]: value,
      }));
   };

   // NOTE: need to use this and maybe move to a different component | might not need this because we are just sending a POST request
   const addItem = (newItem) => {
      setItems((items) => [
         ...items,
         { ...newItem, id: formData.name.toLowerCase() },
      ]);
   };

   const history = useHistory();
   // const navigate = useNavigate();

   const handleSubmit = (event) => {
      event.preventDefault();
      // SnackOrBoozeApi.addItem({ ...formData });
      // addColor({ ...formData }); //being brought down from ShoppingList
      // history.push('/'); //back to the home page after form is submitted | NOTE: might not work without function call?
   };

   return (
      <form
         className="NewItemForm"
         onSubmit={handleSubmit}
      >
         <div className="NewItemForm-input">
            <label htmlFor="name">Name</label>
            <input
               id="name"
               type="text"
               name="name"
               placeholder="Name"
               value={formData.name}
               onChange={handleChange}
            />
         </div>

         <div className="NewItemForm-input">
            <label htmlFor="description">Description</label>
            <input
               id="description"
               type="text"
               name="description"
               placeholder="Description"
               value={formData.description}
               onChange={handleChange}
            />
         </div>

         <div className="NewItemForm-input">
            <label htmlFor="recipe">Recipe</label>
            <input
               id="recipe"
               type="text"
               name="recipe"
               placeholder="Recipe"
               value={formData.recipe}
               onChange={handleChange}
            />
         </div>

         <div className="NewItemForm-input">
            <label htmlFor="serve">Serve</label>
            <input
               id="serve"
               type="text"
               name="serve"
               placeholder="Serve"
               value={formData.serve}
               onChange={handleChange}
            />
         </div>

         <div className="NewItemForm-buttons">
            <button className="add">Add Item</button>
            <button
               className="cancel"
               onClick={() => history.goBack()}
            >
               Cancel
            </button>
         </div>
      </form>
   );
};

export default NewItemForm;
